import React, { useState, useRef } from 'react';
import { Button } from 'react-bootstrap';
import Webcam from 'react-webcam';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import LoadingScreen from 'src/LoadingScreen'; // Assume you've created this
import ResultScreen from './ResultScreen'; // Assume you've created this

function App() {
  const [buttonClicked, setButtonClicked] = useState('중');
  const [photoData, setPhotoData] = useState(null);
  const [isCameraOn, setIsCameraOn] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [lastPhoto, setLastPhoto] = useState(null);
  const [ocrText, setOcrText] = useState('');
  const [openaiSummary, setOpenaiSummary] = useState('');
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  const handleClick = (value) => {
    setButtonClicked(value);
    if (lastPhoto) {
      handleSendPhoto(); // Automatically send photo when difficulty is changed if photo exists
    }
  };

  const handleTakePhoto = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const webcamVideo = webcamRef.current.video;
    canvas.width = webcamVideo.videoWidth;
    canvas.height = webcamVideo.videoHeight;
    context.drawImage(webcamVideo, 0, 0, canvas.width, canvas.height);
    const photoDataURL = canvas.toDataURL('image/png');
    setPhotoData(photoDataURL);
    setLastPhoto(photoDataURL);
  };

  const handleSavePhoto = () => {
    const a = document.createElement('a');
    a.href = photoData;
    a.download = 'photo.png';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const handleSendPhoto = async () => {
    if (!lastPhoto) {
      console.error('No photo data to send');
      return;
    }
    setIsLoading(true);

    const blob = await (await fetch(lastPhoto)).blob();
    const formData = new FormData();
    formData.append('image', blob, 'photo.png');
    formData.append('level', buttonClicked);

    fetch('http://localhost:5000/process-image', {
      method: 'POST',
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        setOcrText(data.ocr_text);
        setOpenaiSummary(data.openai_summary);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error:', error);
        setIsLoading(false);
      });
  };

  const handleRetryWithNewLevel = (newLevel) => {
    setButtonClicked(newLevel);
    handleSendPhoto(); // Re-send photo with new difficulty level
  };


  if (isLoading) {
    return <LoadingScreen />;
  } else if (ocrText) {
    return <ResultScreen ocrText={ocrText} openaiSummary={openaiSummary} onLevelChange={handleRetryWithNewLevel} />;
  } else {
  return (
    <div className="phone-screen">
      <div className="phone-content">
        <h1 className="title">신문 요약 서비스</h1>
        <p className="tip">TIP: 요약하고 싶은 글이 있나요? 난이도 상 중 하를 선택해서 요약 수준을 바꿔보세요.</p>
        <div className="button-container">
          <Button variant={buttonClicked === '상' ? "danger" : "outline-danger"} onClick={() => handleClick('상')}>
            상
          </Button>
          <Button variant={buttonClicked === '중' ? "warning" : "outline-warning"} onClick={() => handleClick('중')}>
            중
          </Button>
          <Button variant={buttonClicked === '하' ? "primary" : "outline-primary"} onClick={() => handleClick('하')}>
            하
          </Button>
        </div>
        {buttonClicked && <p className="selected-button">선택한 버튼: {buttonClicked}</p>}
        <div className="camera-container">
          {isCameraOn && (
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/png"
              style={{ display: 'block', marginBottom: '10px' }}
            />
          )}
          <canvas
            ref={canvasRef}
            style={{ display: 'none' }}
          ></canvas>
          <Button
            className="camera-button"
            variant="warning"
            onClick={handleTakePhoto}
          >
            사진 찍기
          </Button>
          {photoData && (
            <Button
              className="save-button"
              variant="light"
              onClick={handleSavePhoto}
            >
              사진 저장
            </Button>
          )}
          <Button
            variant="secondary"
            onClick={toggleCamera}
          >
            카메라 {isCameraOn ? '끄기' : '켜기'}
          </Button>
        </div>
        {photoData && (
          <div className="photo-container">
            <h2>찍은 사진</h2>
            <img src={photoData} alt="찍은 사진" />
          </div>
        )}
      </div>
      <div className="bottom-buttons">
        <Button variant="info" onClick={() => handleRedirect('https://www.example.com')}>
          외부 링크로 이동
        </Button>
      </div>
    </div>
  );
}
}

export default App;
